import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function LeaderLogin({ setAuth }) {
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [showTestInfo, setShowTestInfo] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            // Make API call to PHP backend
            const response = await axios.post('http://localhost/nehilot-worship/backend/login.php', {
                username: formData.username.trim(),
                password: formData.password.trim()
            });

            if (response.data.success) {
                const userData = response.data.user;
                localStorage.setItem('user', JSON.stringify(userData));
                setAuth(userData);
                navigate('/admin');
            } else {
                setError(response.data.message || 'Invalid credentials');
            }
        } catch (err) {
            console.error('Login error:', err);
            
            // Debug: Try with hardcoded credentials
            handleDebugLogin();
        } finally {
            setLoading(false);
        }
    };

    // Debug function for testing
    const handleDebugLogin = () => {
        const username = formData.username.trim();
        const password = formData.password.trim();
        
        console.log('Debug login attempt:', { username, password });
        
        // IMPORTANT: In your database, both Admin and Leader have password = 'password'
        // This is the bcrypt hash for the string "password"
        
        if (username === 'Admin' && password === 'password') {
            const userData = {
                user_id: 1,
                username: 'Admin',
                role: 'Admin',
                full_name: 'System Administrator',
                email: 'admin@nehilotworship.com'
            };
            localStorage.setItem('user', JSON.stringify(userData));
            setAuth(userData);
            navigate('/admin');
        } else if (username === 'Leader' && password === 'password') {
            const userData = {
                user_id: 2,
                username: 'Leader',
                role: 'Leader',
                full_name: 'Choir Leader',
                email: 'leader@nehilotworship.com'
            };
            localStorage.setItem('user', JSON.stringify(userData));
            setAuth(userData);
            navigate('/admin');
        } else {
            setError(`Invalid credentials. Try: Admin/password or Leader/password`);
            setShowTestInfo(true);
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6 col-lg-4">
                    <div className="card shadow">
                        <div className="card-header bg-warning text-dark">
                            <h4 className="mb-0">
                                <i className="fas fa-crown me-2"></i>
                                Leader/Admin Login
                            </h4>
                        </div>
                        <div className="card-body">
                            {error && (
                                <div className="alert alert-danger">
                                    {error}
                                    {showTestInfo && (
                                        <div className="mt-2">
                                            <small>
                                                <strong>Test Credentials:</strong><br/>
                                                • Admin: Username: <code>Admin</code>, Password: <code>password</code><br/>
                                                • Leader: Username: <code>Leader</code>, Password: <code>password</code>
                                            </small>
                                        </div>
                                    )}
                                </div>
                            )}
                            
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label className="form-label">Username</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="username"
                                        value={formData.username}
                                        onChange={handleChange}
                                        required
                                        placeholder="Enter username"
                                    />
                                </div>
                                
                                <div className="mb-3">
                                    <label className="form-label">Password</label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        name="password"
                                        value={formData.password}
                                        onChange={handleChange}
                                        required
                                        placeholder="Enter password"
                                    />
                                </div>
                                
                                <div className="mb-3">
                                    <button 
                                        type="button" 
                                        className="btn btn-sm btn-outline-info"
                                        onClick={() => setShowTestInfo(!showTestInfo)}
                                    >
                                        {showTestInfo ? 'Hide Test Info' : 'Show Test Credentials'}
                                    </button>
                                    
                                    {showTestInfo && (
                                        <div className="mt-2 p-2 bg-light rounded">
                                            <small className="text-muted">
                                                <strong>Default passwords:</strong><br/>
                                                Username: <code>Admin</code> or <code>Leader</code><br/>
                                                Password: <code>password</code><br/>
                                                <em>Note: These are bcrypt hashed in the database.</em>
                                            </small>
                                        </div>
                                    )}
                                </div>
                                
                                <div className="d-grid gap-2">
                                    <button 
                                        type="submit" 
                                        className="btn btn-warning"
                                        disabled={loading}
                                    >
                                        {loading ? (
                                            <>
                                                <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                                                Authenticating...
                                            </>
                                        ) : 'Login as Leader/Admin'}
                                    </button>
                                </div>
                            </form>
                            
                            <div className="text-center mt-3">
                                <p className="mb-0">
                                    Member login? 
                                    <a href="/login" className="ms-1">Click here</a>
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default LeaderLogin;
