import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function LeaderLogin({ setAuth }) {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        role: 'Leader'
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            // Make API call to your backend
            const response = await axios.post('http://localhost:8000/api/leader-login', {
                username: formData.username,
                password: formData.password
            });

            if (response.data.success) {
                const userData = response.data.user;
                localStorage.setItem('user', JSON.stringify(userData));
                setAuth(userData);
                
                // Redirect based on role
                if (userData.role === 'Admin' || userData.role === 'Leader') {
                    navigate('/admin');
                } else {
                    navigate('/dashboard');
                }
            } else {
                setError(response.data.message || 'Invalid credentials');
            }
        } catch (err) {
            console.error('Login error:', err);
            
            // Fallback to hardcoded credentials if API is not available
            if (err.message.includes('Network Error') || err.response?.status === 404) {
                handleFallbackLogin();
            } else {
                setError(err.response?.data?.message || 'Login failed. Please try again.');
            }
        } finally {
            setLoading(false);
        }
    };

    // Fallback method - only used if API is not available
    const handleFallbackLogin = () => {
        const username = formData.username.trim();
        const password = formData.password.trim();
        
        // Database credentials from your SQL file
        if (username === 'Admin' && password === 'Admin@Nahilot') {
            const userData = {
                user_id: 1,
                username: 'Admin',
                role: 'Admin',
                full_name: 'System Administrator',
                email: 'admin@nehilotworship.com'
            };
            localStorage.setItem('user', JSON.stringify(userData));
            setAuth(userData);
            navigate('/admin');
        } else if (username === 'Leader' && password === 'Nehilot@2024') {
            const userData = {
                user_id: 100,
                username: 'Leader',
                role: 'Leader',
                full_name: 'Choir Leader',
                email: 'leader@nehilotworship.com'
            };
            localStorage.setItem('user', JSON.stringify(userData));
            setAuth(userData);
            navigate('/admin');
        } else {
            setError('Invalid credentials. Please check username and password.');
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6 col-lg-4">
                    <div className="card shadow">
                        <div className="card-header bg-warning text-dark">
                            <h4 className="mb-0">
                                <i className="fas fa-crown me-2"></i>
                                Leader/Admin Login
                            </h4>
                        </div>
                        <div className="card-body">
                            {error && (
                                <div className="alert alert-danger">{error}</div>
                            )}
                            
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label className="form-label">Username</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="username"
                                        value={formData.username}
                                        onChange={handleChange}
                                        required
                                        placeholder="Enter username"
                                    />
                                </div>
                                
                                <div className="mb-3">
                                    <label className="form-label">Password</label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        name="password"
                                        value={formData.password}
                                        onChange={handleChange}
                                        required
                                        placeholder="Enter password"
                                    />
                                </div>
                                
                                <div className="mb-3">
                                    <div className="alert alert-info p-2">
                                        <small className="d-block mb-1">
                                            <strong>Note:</strong> Use your administrator credentials provided by the system.
                                        </small>
                                        <small className="text-danger">
                                            <i className="fas fa-exclamation-triangle me-1"></i>
                                            Do not share your login details with unauthorized persons.
                                        </small>
                                    </div>
                                </div>
                                
                                <div className="d-grid gap-2">
                                    <button 
                                        type="submit" 
                                        className="btn btn-warning"
                                        disabled={loading}
                                    >
                                        {loading ? (
                                            <>
                                                <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                                                Authenticating...
                                            </>
                                        ) : 'Login as Leader/Admin'}
                                    </button>
                                </div>
                            </form>
                            
                            <div className="text-center mt-3">
                                <p className="mb-0">
                                    Member login? 
                                    <a href="/login" className="ms-1">Click here</a>
                                </p>
                                <small className="text-muted">
                                    <i className="fas fa-shield-alt me-1"></i>
                                    Secure login system
                                </small>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default LeaderLogin;
