import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
    const [currentFeature, setCurrentFeature] = useState(0);
    const [animatedFeatures, setAnimatedFeatures] = useState(Array(6).fill(false));
    const [isHeroVisible, setIsHeroVisible] = useState(false);

    const features = [
        {
            icon: 'fas fa-user-plus',
            title: 'Member Registration',
            description: 'Easy online registration for new choir members with instant approval system.',
            color: 'from-purple-500 to-pink-500'
        },
        {
            icon: 'fas fa-calendar-check',
            title: 'Event Management',
            description: 'Schedule and manage rehearsals, performances, and special events seamlessly.',
            color: 'from-blue-500 to-cyan-500'
        },
        {
            icon: 'fas fa-clipboard-check',
            title: 'Attendance Tracking',
            description: 'Digital attendance system with real-time reports and analytics.',
            color: 'from-green-500 to-emerald-500'
        },
        {
            icon: 'fas fa-chart-line',
            title: 'Admin Dashboard',
            description: 'Comprehensive admin panel for complete choir management control.',
            color: 'from-orange-500 to-red-500'
        },
        {
            icon: 'fas fa-users-cog',
            title: 'Member Dashboard',
            description: 'Personal dashboard for members to track participation and events.',
            color: 'from-indigo-500 to-purple-500'
        },
        {
            icon: 'fas fa-bullhorn',
            title: 'Announcements',
            description: 'Instant announcements and notifications for all choir members.',
            color: 'from-teal-500 to-cyan-500'
        }
    ];

    const testimonials = [
        {
            quote: "The Nehilot Worship system has completely transformed how we manage our choir. Everything is so organized now!",
            author: "Sarah Johnson",
            role: "Choir Director",
            avatar: "SJ"
        },
        {
            quote: "As a choir member, I love how easy it is to check schedules and mark my attendance. It's made my experience so much better!",
            author: "Michael Chen",
            role: "Tenor Section Lead",
            avatar: "MC"
        },
        {
            quote: "The analytics and reporting features have saved me hours of administrative work every week. Highly recommended!",
            author: "David Miller",
            role: "Music Administrator",
            avatar: "DM"
        }
    ];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentFeature((prev) => (prev + 1) % features.length);
        }, 4000);
        return () => clearInterval(interval);
    }, []);

    useEffect(() => {
        // Trigger hero animation on mount
        setIsHeroVisible(true);
        
        // Trigger feature animations sequentially
        const timers = features.map((_, index) => 
            setTimeout(() => {
                setAnimatedFeatures(prev => {
                    const newArr = [...prev];
                    newArr[index] = true;
                    return newArr;
                });
            }, index * 200)
        );
        
        return () => timers.forEach(timer => clearTimeout(timer));
    }, []);

    return (
        <>
            {/* Hero Section - Enhanced with more visual elements */}
            <section className={`hero-section relative min-h-screen flex items-center justify-center overflow-hidden transition-all duration-1000 ${isHeroVisible ? 'opacity-100' : 'opacity-0'}`}
                     style={{
                         background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%)'
                     }}>
                {/* Animated Background Particles */}
                <div className="absolute inset-0 overflow-hidden">
                    {[...Array(20)].map((_, i) => (
                        <div key={i} 
                             className="absolute rounded-full bg-white opacity-10 animate-float"
                             style={{
                                 width: `${Math.random() * 100 + 20}px`,
                                 height: `${Math.random() * 100 + 20}px`,
                                 left: `${Math.random() * 100}%`,
                                 top: `${Math.random() * 100}%`,
                                 animationDelay: `${Math.random() * 5}s`,
                                 animationDuration: `${Math.random() * 20 + 10}s`
                             }}>
                        </div>
                    ))}
                </div>

                {/* Floating Music Notes */}
                <div className="absolute inset-0">
                    {['♩', '♪', '♫', '♬'].map((note, i) => (
                        <div key={i}
                             className="absolute text-white opacity-20 text-3xl animate-bounce"
                             style={{
                                 left: `${20 + i * 20}%`,
                                 top: `${30 + Math.sin(i) * 40}%`,
                                 animationDelay: `${i * 0.5}s`,
                                 animationDuration: '3s'
                             }}>
                            {note}
                        </div>
                    ))}
                </div>

                <div className="container relative z-10 py-20">
                    <div className="row align-items-center">
                        <div className="col-lg-7 mb-10 lg:mb-0">
                            <div className={`transform transition-all duration-700 ${isHeroVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
                                <span className="inline-block px-4 py-2 mb-6 rounded-full bg-white/20 backdrop-blur-sm text-white text-sm font-semibold animate-pulse">
                                    <i className="fas fa-star me-2 text-yellow-300"></i>
                                    Premium Choir Management Platform
                                </span>
                                
                                <h1 className="text-white font-bold text-5xl lg:text-7xl mb-6 leading-tight">
                                    Welcome to 
                                    <span className="block bg-gradient-to-r from-yellow-300 to-pink-400 bg-clip-text text-transparent mt-2">
                                        Nehilot Worship
                                    </span>
                                </h1>
                                
                                <p className="text-white/90 text-xl mb-10 leading-relaxed max-w-2xl">
                                    Experience the transformative power of worship through music. Join our vibrant community 
                                    at IPRC Tumba and elevate your spiritual journey with cutting-edge choir management.
                                </p>
                                
                                <div className="flex flex-wrap gap-5 mb-12">
                                    <Link to="/register" 
                                          className="group relative px-8 py-4 bg-gradient-to-r from-yellow-400 to-orange-500 rounded-xl font-bold text-white text-lg shadow-2xl hover:shadow-3xl transform hover:-translate-y-1 transition-all duration-300">
                                        <span className="relative z-10 flex items-center">
                                            <i className="fas fa-crown text-xl mr-3"></i>
                                            Join Our Choir
                                        </span>
                                        <div className="absolute inset-0 bg-gradient-to-r from-orange-500 to-red-500 rounded-xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                                    </Link>
                                    
                                    <Link to="/login" 
                                          className="group px-8 py-4 bg-white/10 backdrop-blur-sm border-2 border-white/30 rounded-xl font-bold text-white text-lg hover:bg-white/20 transform hover:-translate-y-1 transition-all duration-300">
                                        <i className="fas fa-sign-in-alt mr-3"></i>
                                        Member Login
                                    </Link>
                                </div>
                                
                                {/* Live Stats Counter */}
                                <div className="grid grid-cols-3 gap-6 max-w-md">
                                    <div className="text-center p-4 bg-white/10 backdrop-blur-sm rounded-xl border border-white/20">
                                        <div className="text-4xl font-bold text-white mb-1 animate-count-up" data-count="500">0</div>
                                        <div className="text-white/80">Active Members</div>
                                    </div>
                                    <div className="text-center p-4 bg-white/10 backdrop-blur-sm rounded-xl border border-white/20">
                                        <div className="text-4xl font-bold text-white mb-1 animate-count-up" data-count="150">0</div>
                                        <div className="text-white/80">Events Hosted</div>
                                    </div>
                                    <div className="text-center p-4 bg-white/10 backdrop-blur-sm rounded-xl border border-white/20">
                                        <div className="text-4xl font-bold text-white mb-1 animate-count-up" data-count="99">0</div>
                                        <div className="text-white/80">Satisfaction Rate</div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        
                        <div className="col-lg-5 relative">
                            <div className={`transform transition-all duration-1000 delay-300 ${isHeroVisible ? 'translate-x-0 opacity-100' : 'translate-x-10 opacity-0'}`}>
                                {/* Animated Music Visualization */}
                                <div className="relative">
                                    <div className="absolute inset-0 bg-gradient-to-br from-purple-500/20 to-pink-500/20 rounded-full blur-3xl"></div>
                                    
                                    <div className="relative bg-gradient-to-br from-white/10 to-white/5 backdrop-blur-xl rounded-3xl p-8 border border-white/20 shadow-2xl">
                                        {/* Music Bars Animation */}
                                        <div className="flex justify-center items-end h-40 mb-8 space-x-2">
                                            {[40, 60, 80, 100, 80, 60, 40].map((height, index) => (
                                                <div key={index}
                                                     className="w-3 bg-gradient-to-t from-yellow-400 to-orange-500 rounded-t-lg animate-music-bar"
                                                     style={{
                                                         height: `${height}%`,
                                                         animationDelay: `${index * 0.1}s`
                                                     }}>
                                                </div>
                                            ))}
                                        </div>
                                        
                                        <div className="text-center">
                                            <i className="fas fa-music text-white text-6xl mb-4 animate-pulse"></i>
                                            <h3 className="text-white text-2xl font-bold mb-2">Live Worship Session</h3>
                                            <p className="text-white/70 mb-4">Join us every Sunday at 10 AM</p>
                                            <button className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-green-500 to-emerald-600 rounded-full text-white font-bold hover:shadow-lg transform hover:scale-105 transition-all duration-300">
                                                <i className="fas fa-play-circle mr-3 text-xl"></i>
                                                Watch Live Now
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                
                {/* Scroll Indicator */}
                <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
                    <div className="text-white text-center">
                        <div className="text-sm mb-2">Scroll to Explore</div>
                        <i className="fas fa-chevron-down text-xl"></i>
                    </div>
                </div>
            </section>

            {/* Features Section - Enhanced with Gradient Cards */}
            <section className="py-20 bg-gradient-to-b from-gray-50 to-white relative overflow-hidden">
                {/* Background Pattern */}
                <div className="absolute inset-0 opacity-5">
                    <div className="absolute top-0 left-0 w-72 h-72 bg-purple-300 rounded-full mix-blend-multiply filter blur-3xl animate-pulse"></div>
                    <div className="absolute top-0 right-0 w-72 h-72 bg-yellow-300 rounded-full mix-blend-multiply filter blur-3xl animate-pulse" style={{animationDelay: '2s'}}></div>
                    <div className="absolute bottom-0 left-1/2 w-72 h-72 bg-pink-300 rounded-full mix-blend-multiply filter blur-3xl animate-pulse" style={{animationDelay: '4s'}}></div>
                </div>

                <div className="container relative z-10">
                    <div className="text-center mb-16">
                        <span className="inline-block px-6 py-2 mb-4 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 text-white text-sm font-bold">
                            <i className="fas fa-gem mr-2"></i>
                            EXCLUSIVE FEATURES
                        </span>
                        <h2 className="text-4xl lg:text-5xl font-bold text-gray-900 mb-6">
                            Everything You Need for 
                            <span className="block text-transparent bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text">
                                Choir Excellence
                            </span>
                        </h2>
                        <p className="text-gray-600 text-xl max-w-3xl mx-auto">
                            Our comprehensive platform provides all the tools needed to manage, organize, and grow your choir community effectively.
                        </p>
                    </div>
                    
                    {/* Interactive Feature Carousel */}
                    <div className="mb-16">
                        <div className="relative bg-gradient-to-br from-gray-900 via-purple-900 to-violet-900 rounded-3xl p-8 shadow-2xl overflow-hidden">
                            <div className="absolute top-0 right-0 w-64 h-64 bg-gradient-to-br from-pink-500/20 to-purple-500/20 rounded-full blur-3xl"></div>
                            
                            <div className="relative z-10">
                                <div className="flex flex-col lg:flex-row items-center justify-between">
                                    <div className="lg:w-2/3 mb-10 lg:mb-0">
                                        <div className={`transition-all duration-500 ${animatedFeatures[currentFeature] ? 'opacity-100' : 'opacity-0'}`}>
                                            <div className="flex items-center mb-6">
                                                <div className={`w-20 h-20 rounded-2xl bg-gradient-to-br ${features[currentFeature].color} flex items-center justify-center mr-6 shadow-lg`}>
                                                    <i className={`${features[currentFeature].icon} text-white text-3xl`}></i>
                                                </div>
                                                <div>
                                                    <span className="inline-block px-3 py-1 rounded-full bg-white/20 text-white text-xs font-bold mb-2">
                                                        FEATURE {currentFeature + 1}
                                                    </span>
                                                    <h3 className="text-3xl font-bold text-white">{features[currentFeature].title}</h3>
                                                </div>
                                            </div>
                                            <p className="text-white/80 text-lg mb-8">
                                                {features[currentFeature].description}
                                            </p>
                                            <Link to="/features" className="inline-flex items-center text-white font-bold hover:text-yellow-300 transition-colors duration-300">
                                                Explore Feature <i className="fas fa-arrow-right ml-2"></i>
                                            </Link>
                                        </div>
                                    </div>
                                    
                                    <div className="lg:w-1/3">
                                        <div className="flex lg:flex-col justify-center space-x-4 lg:space-x-0 lg:space-y-4">
                                            {features.map((_, index) => (
                                                <button
                                                    key={index}
                                                    onClick={() => setCurrentFeature(index)}
                                                    className={`w-3 h-3 lg:w-full lg:h-12 rounded-lg lg:rounded-xl flex items-center transition-all duration-300 ${index === currentFeature ? 'bg-gradient-to-r from-yellow-400 to-orange-500 lg:px-4' : 'bg-white/30 hover:bg-white/50'}`}
                                                >
                                                    {index === currentFeature && (
                                                        <span className="hidden lg:block text-white font-bold text-sm">
                                                            {features[index].title}
                                                        </span>
                                                    )}
                                                </button>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    
                    {/* Features Grid with Hover Effects */}
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                        {features.map((feature, index) => (
                            <div key={index}
                                 className={`transform transition-all duration-500 ${animatedFeatures[index] ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
                                 style={{transitionDelay: `${index * 100}ms`}}>
                                <div className="group h-full bg-white rounded-2xl p-8 shadow-xl hover:shadow-2xl border border-gray-100 hover:border-transparent transition-all duration-300 hover:-translate-y-2">
                                    <div className="relative mb-6">
                                        <div className={`absolute inset-0 bg-gradient-to-br ${feature.color} rounded-2xl opacity-10 group-hover:opacity-20 transition-opacity duration-300`}></div>
                                        <div className={`relative w-16 h-16 rounded-xl bg-gradient-to-br ${feature.color} flex items-center justify-center shadow-lg`}>
                                            <i className={`${feature.icon} text-white text-2xl`}></i>
                                        </div>
                                    </div>
                                    
                                    <h3 className="text-xl font-bold text-gray-900 mb-3 group-hover:text-transparent group-hover:bg-clip-text group-hover:bg-gradient-to-r group-hover:from-blue-600 group-hover:to-purple-600 transition-all duration-300">
                                        {feature.title}
                                    </h3>
                                    
                                    <p className="text-gray-600 mb-6">
                                        {feature.description}
                                    </p>
                                    
                                    <div className="flex items-center justify-between">
                                        <Link to={`/feature/${index}`} 
                                              className="inline-flex items-center text-sm font-bold text-gray-700 hover:text-blue-600 transition-colors duration-300">
                                            Learn More
                                            <i className="fas fa-arrow-right ml-2 transform group-hover:translate-x-2 transition-transform duration-300"></i>
                                        </Link>
                                        <div className="text-xs px-3 py-1 rounded-full bg-gradient-to-r from-gray-100 to-gray-50 text-gray-600 font-medium">
                                            Active
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                    
                    {/* Feature Comparison */}
                    <div className="mt-20 bg-gradient-to-r from-blue-50 to-cyan-50 rounded-3xl p-8 border border-blue-100">
                        <div className="text-center mb-8">
                            <h3 className="text-2xl font-bold text-gray-900 mb-4">Why Choose Our Platform?</h3>
                            <p className="text-gray-600">Compare with traditional choir management methods</p>
                        </div>
                        
                        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                            {[
                                { title: 'Efficiency', value: '90%', description: 'Time saved on administrative tasks' },
                                { title: 'Accuracy', value: '99.9%', description: 'Error-free attendance tracking' },
                                { title: 'Engagement', value: '75%', description: 'Increased member participation' }
                            ].map((stat, index) => (
                                <div key={index} className="bg-white rounded-xl p-6 text-center shadow-md hover:shadow-lg transition-shadow duration-300">
                                    <div className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-2">
                                        {stat.value}
                                    </div>
                                    <div className="text-lg font-bold text-gray-900 mb-1">{stat.title}</div>
                                    <div className="text-gray-600 text-sm">{stat.description}</div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </section>

            {/* Admin & Dashboard Section */}
            <section className="py-20 bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white relative overflow-hidden">
                <div className="absolute inset-0 bg-grid-white/[0.02] bg-[size:50px_50px]"></div>
                
                <div className="container relative z-10">
                    <div className="row items-center">
                        <div className="col-lg-6 mb-12 lg:mb-0">
                            <div className="max-w-2xl">
                                <span className="inline-block px-4 py-2 mb-6 rounded-full bg-gradient-to-r from-cyan-500 to-blue-500 text-white text-sm font-bold">
                                    <i className="fas fa-shield-alt mr-2"></i>
                                    ADMIN POWER
                                </span>
                                
                                <h2 className="text-4xl lg:text-5xl font-bold mb-6">
                                    Complete Control with 
                                    <span className="block text-transparent bg-gradient-to-r from-cyan-400 to-blue-400 bg-clip-text">
                                        Advanced Dashboard
                                    </span>
                                </h2>
                                
                                <p className="text-gray-300 text-lg mb-8 leading-relaxed">
                                    Take full command of your choir management with our powerful admin dashboard. 
                                    Real-time analytics, comprehensive reports, and intuitive controls at your fingertips.
                                </p>
                                
                                <div className="space-y-4 mb-10">
                                    {[
                                        'Real-time member activity tracking',
                                        'Automated report generation',
                                        'Advanced permission management',
                                        'Bulk operations and imports',
                                        'Custom notification systems',
                                        'Data export in multiple formats'
                                    ].map((item, index) => (
                                        <div key={index} className="flex items-center">
                                            <div className="w-8 h-8 rounded-full bg-gradient-to-r from-green-500 to-emerald-500 flex items-center justify-center mr-4">
                                                <i className="fas fa-check text-white text-sm"></i>
                                            </div>
                                            <span className="text-gray-200">{item}</span>
                                        </div>
                                    ))}
                                </div>
                                
                                <div className="flex flex-wrap gap-4">
                                    <Link to="/admin/demo" 
                                          className="px-8 py-4 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-xl font-bold hover:shadow-2xl transform hover:-translate-y-1 transition-all duration-300 flex items-center">
                                        <i className="fas fa-play-circle mr-3 text-xl"></i>
                                        View Demo
                                    </Link>
                                    <Link to="/login" 
                                          className="px-8 py-4 bg-white/10 backdrop-blur-sm border border-white/20 rounded-xl font-bold hover:bg-white/20 transform hover:-translate-y-1 transition-all duration-300 flex items-center">
                                        <i className="fas fa-sign-in-alt mr-3"></i>
                                        Admin Login
                                    </Link>
                                </div>
                            </div>
                        </div>
                        
                        <div className="col-lg-6">
                            {/* Dashboard Preview */}
                            <div className="relative bg-gradient-to-br from-gray-800 to-gray-900 rounded-3xl p-1 shadow-2xl border border-gray-700">
                                {/* Dashboard Mockup */}
                                <div className="bg-gray-900 rounded-2xl p-6">
                                    {/* Dashboard Header */}
                                    <div className="flex items-center justify-between mb-8">
                                        <div className="flex items-center">
                                            <div className="w-10 h-10 rounded-full bg-gradient-to-r from-cyan-500 to-blue-500 flex items-center justify-center mr-3">
                                                <i className="fas fa-chart-line text-white"></i>
                                            </div>
                                            <div>
                                                <div className="text-white font-bold">Admin Dashboard</div>
                                                <div className="text-gray-400 text-sm">Real-time Analytics</div>
                                            </div>
                                        </div>
                                        <div className="text-sm text-green-400 font-bold animate-pulse">
                                            <i className="fas fa-circle mr-2"></i>
                                            Live
                                        </div>
                                    </div>
                                    
                                    {/* Stats Grid */}
                                    <div className="grid grid-cols-2 gap-4 mb-6">
                                        {[
                                            { label: 'Active Members', value: '487', change: '+12%', color: 'text-green-400' },
                                            { label: 'Today\'s Attendance', value: '89%', change: '+5%', color: 'text-blue-400' },
                                            { label: 'Upcoming Events', value: '7', change: '+2', color: 'text-yellow-400' },
                                            { label: 'Pending Requests', value: '23', change: '-8', color: 'text-red-400' }
                                        ].map((stat, index) => (
                                            <div key={index} className="bg-gray-800/50 rounded-xl p-4 border border-gray-700">
                                                <div className="text-gray-400 text-sm mb-1">{stat.label}</div>
                                                <div className="flex items-end justify-between">
                                                    <div className="text-2xl font-bold text-white">{stat.value}</div>
                                                    <div className={`text-sm font-bold ${stat.color}`}>{stat.change}</div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                    
                                    {/* Chart Area */}
                                    <div className="bg-gray-800/50 rounded-xl p-4 border border-gray-700 mb-4">
                                        <div className="flex items-center justify-between mb-4">
                                            <div className="text-white font-bold">Member Activity</div>
                                            <div className="text-gray-400 text-sm">Last 30 days</div>
                                        </div>
                                        {/* Chart Bars */}
                                        <div className="flex items-end h-24 space-x-1">
                                            {[40, 65, 80, 60, 75, 90, 70].map((height, index) => (
                                                <div key={index} className="flex-1">
                                                    <div className={`h-${height/10} rounded-t bg-gradient-to-t ${index % 2 === 0 ? 'from-cyan-500/60 to-cyan-500' : 'from-blue-500/60 to-blue-500'} animate-chart-bar`}
                                                         style={{height: `${height}%`}}>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    </div>
                                    
                                    {/* Recent Activity */}
                                    <div className="text-sm text-gray-400">
                                        <div className="flex items-center justify-between mb-2">
                                            <div>Recent Activity</div>
                                            <div className="text-cyan-400 cursor-pointer hover:text-cyan-300">
                                                View All <i className="fas fa-arrow-right ml-1"></i>
                                            </div>
                                        </div>
                                        <div className="space-y-3">
                                            {[
                                                { action: 'New member registered', time: '2 min ago' },
                                                { action: 'Event created: Sunday Service', time: '1 hour ago' },
                                                { action: 'Attendance marked for 45 members', time: '3 hours ago' }
                                            ].map((activity, index) => (
                                                <div key={index} className="flex items-center">
                                                    <div className="w-2 h-2 rounded-full bg-green-500 mr-3"></div>
                                                    <div className="flex-1 text-gray-300">{activity.action}</div>
                                                    <div className="text-gray-500">{activity.time}</div>
                                                </div>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>

            {/* Testimonials Section */}
            <section className="py-20 bg-gradient-to-b from-white to-gray-50">
                <div className="container">
                    <div className="text-center mb-16">
                        <span className="inline-block px-6 py-2 mb-4 rounded-full bg-gradient-to-r from-pink-500 to-rose-500 text-white text-sm font-bold">
                            <i className="fas fa-heart mr-2"></i>
                            LOVE FROM OUR COMMUNITY
                        </span>
                        <h2 className="text-4xl lg:text-5xl font-bold text-gray-900 mb-6">
                            What Our Members Say
                        </h2>
                        <p className="text-gray-600 text-xl max-w-3xl mx-auto">
                            Join thousands of satisfied choir members and directors who transformed their worship experience.
                        </p>
                    </div>
                    
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                        {testimonials.map((testimonial, index) => (
                            <div key={index} 
                                 className="group bg-white rounded-2xl p-8 shadow-xl hover:shadow-2xl border border-gray-100 hover:border-transparent transition-all duration-300 hover:-translate-y-2">
                                <div className="text-yellow-400 text-3xl mb-6">
                                    <i className="fas fa-quote-left opacity-50 group-hover:opacity-100 transition-opacity duration-300"></i>
                                </div>
                                
                                <p className="text-gray-700 text-lg italic mb-8 leading-relaxed">
                                    "{testimonial.quote}"
                                </p>
                                
                                <div className="flex items-center">
                                    <div className="w-14 h-14 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 flex items-center justify-center text-white font-bold text-xl mr-4">
                                        {testimonial.avatar}
                                    </div>
                                    <div>
                                        <div className="font-bold text-gray-900">{testimonial.author}</div>
                                        <div className="text-gray-600">{testimonial.role}</div>
                                        <div className="flex mt-2">
                                            {[...Array(5)].map((_, i) => (
                                                <i key={i} className="fas fa-star text-yellow-400 text-sm mr-1"></i>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            {/* Final CTA Section */}
            <section className="py-20 bg-gradient-to-br from-indigo-600 via-purple-600 to-pink-600 relative overflow-hidden">
                <div className="absolute inset-0 bg-black/10"></div>
                
                {/* Animated Elements */}
                <div className="absolute top-0 left-0 w-64 h-64 bg-white/10 rounded-full blur-3xl animate-pulse"></div>
                <div className="absolute bottom-0 right-0 w-96 h-96 bg-pink-500/20 rounded-full blur-3xl animate-pulse" style={{animationDelay: '1s'}}></div>
                
                <div className="container relative z-10">
                    <div className="max-w-4xl mx-auto text-center">
                        <h2 className="text-4xl lg:text-6xl font-bold text-white mb-8">
                            Ready to Transform Your
                            <span className="block text-yellow-300">Choir Experience?</span>
                        </h2>
                        
                        <p className="text-white/90 text-xl mb-12 max-w-2xl mx-auto leading-relaxed">
                            Join thousands of worship communities who have elevated their management, 
                            engagement, and spiritual connection through our platform.
                        </p>
                        
                        <div className="flex flex-col lg:flex-row gap-6 justify-center items-center">
                            <Link to="/register"
                                  className="group px-12 py-5 bg-gradient-to-r from-yellow-400 to-orange-500 rounded-2xl font-bold text-gray-900 text-xl hover:shadow-3xl transform hover:scale-105 transition-all duration-300 flex items-center">
                                <i className="fas fa-rocket text-2xl mr-4 group-hover:rotate-12 transition-transform duration-300"></i>
                                Start Free Trial - 30 Days
                            </Link>
                            
                            <Link to="/contact"
                                  className="group px-12 py-5 bg-white/10 backdrop-blur-sm border-2 border-white/30 rounded-2xl font-bold text-white text-xl hover:bg-white/20 transform hover:scale-105 transition-all duration-300 flex items-center">
                                <i className="fas fa-calendar-alt text-2xl mr-4 group-hover:animate-bounce"></i>
                                Schedule a Demo
                            </Link>
                        </div>
                        
                        <div className="mt-12 text-white/70 text-sm">
                            <div className="flex flex-wrap justify-center gap-8">
                                <div className="flex items-center">
                                    <i className="fas fa-shield-check text-green-300 mr-3 text-xl"></i>
                                    <span>100% Secure & Private</span>
                                </div>
                                <div className="flex items-center">
                                    <i className="fas fa-clock text-yellow-300 mr-3 text-xl"></i>
                                    <span>24/7 Customer Support</span>
                                </div>
                                <div className="flex items-center">
                                    <i className="fas fa-sync text-blue-300 mr-3 text-xl"></i>
                                    <span>Free Updates Forever</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>

            {/* Global Styles */}
            <style jsx global>{`
                @keyframes float {
                    0%, 100% { transform: translateY(0px); }
                    50% { transform: translateY(-20px); }
                }
                
                @keyframes countUp {
                    from { transform: translateY(20px); opacity: 0; }
                    to { transform: translateY(0); opacity: 1; }
                }
                
                @keyframes musicBar {
                    0%, 100% { height: 40%; }
                    50% { height: 100%; }
                }
                
                @keyframes chartBar {
                    0% { height: 0%; opacity: 0; }
                    100% { height: var(--target-height); opacity: 1; }
                }
                
                .animate-float {
                    animation: float 6s ease-in-out infinite;
                }
                
                .animate-count-up {
                    animation: countUp 1s ease-out forwards;
                }
                
                .animate-music-bar {
                    animation: musicBar 1s ease-in-out infinite;
                }
                
                .animate-chart-bar {
                    animation: chartBar 0.8s ease-out forwards;
                    animation-delay: var(--delay);
                }
                
                /* Smooth scrolling */
                html {
                    scroll-behavior: smooth;
                }
                
                /* Custom scrollbar */
                ::-webkit-scrollbar {
                    width: 10px;
                }
                
                ::-webkit-scrollbar-track {
                    background: #f1f1f1;
                }
                
                ::-webkit-scrollbar-thumb {
                    background: linear-gradient(to bottom, #667eea, #764ba2);
                    border-radius: 5px;
                }
                
                ::-webkit-scrollbar-thumb:hover {
                    background: linear-gradient(to bottom, #764ba2, #667eea);
                }
            `}</style>
        </>
    );
}

export default HomePage;
