import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login({ setAuth }) {
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        // Simulate API call - In real app, replace with axios call
        setTimeout(() => {
            if (formData.username === 'Admin' && formData.password === 'Admin@Nahilot') {
                // Admin login
                setAuth({ username: 'Admin', role: 'Admin' });
                navigate('/dashboard');
            } else if (formData.username && formData.password) {
                // Member login (simulated)
                setAuth({ username: formData.username, role: 'Member' });
                navigate('/dashboard');
            } else {
                setError('Invalid username or password');
            }
            setLoading(false);
        }, 1000);
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6 col-lg-4">
                    <div className="card shadow">
                        <div className="card-header bg-primary text-white">
                            <h4 className="mb-0">Login to Nehilot Worship</h4>
                        </div>
                        <div className="card-body">
                            {error && (
                                <div className="alert alert-danger">{error}</div>
                            )}
                            
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label className="form-label">Username</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="username"
                                        value={formData.username}
                                        onChange={handleChange}
                                        required
                                        placeholder="Enter your username"
                                    />
                                </div>
                                
                                <div className="mb-3">
                                    <label className="form-label">Password</label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        name="password"
                                        value={formData.password}
                                        onChange={handleChange}
                                        required
                                        placeholder="Enter your password"
                                    />
                                </div>
                                
                                <div className="d-grid gap-2">
                                    <button 
                                        type="submit" 
                                        className="btn btn-primary"
                                        disabled={loading}
                                    >
                                        {loading ? 'Logging in...' : 'Login'}
                                    </button>
                                    
                                    {/* <div className="text-center mt-3">
                                        <small>Admin Credentials:</small><br/>
                                        <small>Username: <strong>Admin</strong></small><br/>
                                        <small>Password: <strong>Admin@Nahilot</strong></small>
                                    </div> */}
                                </div>
                            </form>
                            
                            <div className="text-center mt-3">
                                <p className="mb-0">
                                    Don't have an account? 
                                    <a href="/signup" className="ms-1">Sign up</a>
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
