import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './App.css';

// Layout Components
import Header from './components/layout/Header';
import Footer from './components/layout/Footer';

// Auth Components
import Login from './components/auth/Login';
import MemberRegister from './components/auth/MemberRegister';

// Dashboard Components
import AdminDashboard from './components/admin/AdminDashboard';
import MemberDashboard from './components/member/MemberDashboard';

// Page Components
import HomePage from './components/pages/HomePage';
import AboutPage from './components/pages/AboutPage';
import EventsPage from './components/pages/EventsPage';
import MusicLibrary from './components/pages/MusicLibrary';
import GalleryPage from './components/pages/GalleryPage';
import ContactPage from './components/pages/ContactPage';

// Admin Components
import AdminMembers from './components/admin/AdminMembers';
import AdminEvents from './components/admin/AdminEvents';
import AdminAttendance from './components/admin/AdminAttendance';
import AdminReports from './components/admin/AdminReports';

// Member Components
import MemberProfile from './components/member/MemberProfile';
import MemberSuggestions from './components/member/MemberSuggestions';
import MemberAttendance from './components/member/MemberAttendance';

function App() {
  const [auth, setAuth] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is logged in
    const user = localStorage.getItem('user');
    if (user) {
      try {
        setAuth(JSON.parse(user));
      } catch (e) {
        localStorage.removeItem('user');
      }
    }
    setLoading(false);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('user');
    setAuth(null);
    window.location.href = '/login';
  };

  // Protected Route Component
  const ProtectedRoute = ({ children, allowedRoles }) => {
    if (loading) {
      return (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      );
    }
    
    if (!auth) {
      return <Navigate to="/login" />;
    }
    
    if (allowedRoles && !allowedRoles.includes(auth.role)) {
      return <Navigate to="/" />;
    }
    
    return children;
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App d-flex flex-column min-vh-100">
        <Header auth={auth} onLogout={handleLogout} />
        
        <main className="flex-grow-1">
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<HomePage />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/events" element={<EventsPage />} />
            <Route path="/music" element={<MusicLibrary />} />
            <Route path="/gallery" element={<GalleryPage />} />
            <Route path="/contact" element={<ContactPage />} />
            <Route path="/login" element={<Login setAuth={setAuth} />} />
            <Route path="/register" element={<MemberRegister />} />
            <Route path="/join" element={<MemberRegister />} />
            
            {/* Member Routes */}
            <Route path="/dashboard" element={
              <ProtectedRoute allowedRoles={['Member', 'Volunteer', 'Director', 'Admin']}>
                <MemberDashboard />
              </ProtectedRoute>
            } />
            <Route path="/profile" element={
              <ProtectedRoute allowedRoles={['Member', 'Volunteer', 'Director', 'Admin']}>
                <MemberProfile />
              </ProtectedRoute>
            } />
            <Route path="/my-attendance" element={
              <ProtectedRoute allowedRoles={['Member', 'Volunteer', 'Director', 'Admin']}>
                <MemberAttendance />
              </ProtectedRoute>
            } />
            <Route path="/suggestions" element={
              <ProtectedRoute allowedRoles={['Member', 'Volunteer', 'Director', 'Admin']}>
                <MemberSuggestions />
              </ProtectedRoute>
            } />
            
            {/* Admin Routes */}
            <Route path="/admin" element={
              <ProtectedRoute allowedRoles={['Admin', 'Director']}>
                <AdminDashboard />
              </ProtectedRoute>
            } />
            <Route path="/admin/members" element={
              <ProtectedRoute allowedRoles={['Admin', 'Director']}>
                <AdminMembers />
              </ProtectedRoute>
            } />
            <Route path="/admin/events" element={
              <ProtectedRoute allowedRoles={['Admin', 'Director']}>
                <AdminEvents />
              </ProtectedRoute>
            } />
            <Route path="/admin/attendance" element={
              <ProtectedRoute allowedRoles={['Admin', 'Director']}>
                <AdminAttendance />
              </ProtectedRoute>
            } />
            <Route path="/admin/reports" element={
              <ProtectedRoute allowedRoles={['Admin']}>
                <AdminReports />
              </ProtectedRoute>
            } />
            
            {/* 404 Route */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>
        
        <Footer />
      </div>
    </Router>
  );
}

export default App;
